var apiKey = cti.store.schema.metadata.googleMapsApiKey;
var googleMaps = document.createElement('script');

googleMaps.src = 'https://maps.googleapis.com/maps/api/js?key=' + apiKey;
googleMaps.async = true;
googleMaps.defer = true;

document.body.appendChild(googleMaps);

config.content:
cti.store.variables.serviceDir = cti.store.api.servicedirec.servicedirectory;

//if the page object is not undefined
if(cti.store.pages.directory != undefined){

//keyword search

  if(cti.store.pages.directory.text_1 != undefined){
      cti.store.variables.serviceDir = cti.store.variables.serviceDir.filter
      (
      function(o) {return o.organisation.toLowerCase().indexOf(cti.store.pages.directory.text_1.toLowerCase()) > -1 || cti.store.pages.directory.text_1 == "";}
      );
  };

  if(cti.store.pages.directory.select_1 != null)
  {
      cti.store.variables.serviceDir = cti.store.variables.serviceDir.filter
      (
      function(o) {return o.category_id == cti.store.pages.directory.select_1.value;}
      );
  };

  if(cti.store.pages.directory.select_2 != null)
  {
      cti.store.variables.serviceDir = cti.store.variables.serviceDir.filter
      (
      function(o) {return o.region_id == cti.store.pages.directory.select_2.value;}
      );
  };
};
cti.initMap();

//if on service details
if(cti.store.state.currentPage=="serviceDetails"){
	if(cti.store.variables.selectedService.latitude!=null && cti.store.variables.selectedService.longitude!=null){  
    var map = new google.maps.Map(document.getElementById('map2'), {
      zoom: 13,
      center: {lat: Number(cti.store.variables.latitude), lng: Number(cti.store.variables.longitude)}
    });

    var marker;  
    //only show the one marker and centre to it
    marker = new google.maps.Marker({position: {lat: Number(cti.store.variables.selectedService.latitude), lng:Number(cti.store.variables.selectedService.longitude)}, map: map});
    map.panTo(marker.getPosition());
	}
}

else{
var map = new google.maps.Map(document.getElementById('map'), {
  zoom: 10,
  center: {lat: Number(cti.store.variables.latitude), lng: Number(cti.store.variables.longitude)}
});

//show marker for user location
var image = 'user-assets/home-2.png';
var locMarker = new google.maps.Marker({
position: {lat: Number(cti.store.variables.latitude), lng: Number(cti.store.variables.longitude)},
map: map,
icon: image
});

//show a marker for all the items in the directory  
var infowindow = new google.maps.InfoWindow;
var marker, i;

for (i = 0; i < cti.store.variables.serviceDir.length; i++) {  
marker = new google.maps.Marker({position: {lat: Number(cti.store.variables.serviceDir[i].latitude), lng:Number(cti.store.variables.serviceDir[i].longitude)}, map: map});

google.maps.event.addListener(marker, 'click', (function(marker, i) {
    return function() {
      infowindow.setContent('<p>' + cti.store.variables.serviceDir[i].organisation + '<br/>' + cti.store.variables.serviceDir[i].main_telephone + '</p>');
      infowindow.open(map, marker);
    }  
  })(marker, i));
}
}

var _this = this;

if(navigator.geolocation){
    navigator.geolocation.getCurrentPosition(setPosition);
  	//hardcode for browser testing
  	if(cti.store.variables.latitude==undefined){
    	cti.store.variables.latitude = 52.630611;
    	cti.store.variables.longitude = -1.085532;
    }
}else {
    console.log('Geolocation not supported'); 
    //default in values for LHIS
  	cti.store.variables.latitude = 52.630611;
    cti.store.variables.longitude = -1.085532;
    _this.$success();
}

function setPosition(position){
 	 cti.store.variables.latitude = position.coords.latitude;
     cti.store.variables.longitude = position.coords.longitude;
    _this.$success();
}

